	/*
	 * get rights string by user role
	 * */
	function hi_get_rights()
	{
		switch( HIF_ACCESS )
		{
			case 'administrator':
				return 'manage_options';
				break;
			case 'editor':
				return 'edit_pages';
				break;
			case 'author':
				return 'publish_posts';
				break;
			case 'contributor':
				return 'edit_posts';
				break;
			case 'subscriber':
				return 'read';
				break;
			default:
				return 'manage_options';
				break;
		}
	}

	/*
	 * Admin hooks init
	 * */
	function hi_admin_hooks()
	{
		add_filter( 'admin_menu', 'hi_admin_menu' );

		// init ajax actions
		add_filter( 'wp_ajax_hi_ajax_post_action', 'hi_ajax_post_action' );

        //add_action( 'admin_enqueue_scripts', 'hi_admin_scripts' );
	}

	/*
	 * Activate hook callback
	 * */
	function hi_builder_activate()
	{
		$hi_tpls = get_option( HIF_TEMPLATES );
		if( $hi_tpls === false )
		{
			update_option( HIF_TEMPLATES, unserialize( hi_styles_array() ) );
		}
	}

