	/*
	 * Add plugin admin menu
	 * */
	function hi_admin_menu()
	{
		$page_title = $menu_title = __( 'Hover Effects Builder', 'hitd' );

		global $wp_version;
		if( $wp_version >= 3.8 )
		{
			$page = add_menu_page( $page_title, $menu_title, hi_get_rights(), 'hover-effects-builder', 'hi_admin_interface', 'data:image/svg+xml;base64,' );
		}
		else
		{
			$page = add_menu_page( $page_title, $menu_title, hi_get_rights(), 'hover-effects-builder', 'hi_admin_interface', plugins_url( 'hover-effects-builder/assets/img/logo-18.png' ) );
		}
		add_filter( 'admin_print_styles', 'hi_admin_print_styles_fonts' );
		add_filter( 'admin_print_styles-' . $page, 'hi_admin_print_styles' );
		add_filter( 'admin_print_scripts-' . $page, 'hi_admin_print_scripts' );
		add_action( 'load-' . $page, 'hi_admin_help');
	}

	/*
	 * Hi admin help
	 * */
	function hi_admin_help()
	{
		$screen = get_current_screen();

		$help_text = __('<p>If we can be of any service, don’t hesitate to let us know and we’ll provide you with reliable, efficient help at critical times.</p>', 'hitd'  );


		// Add my_help_tab if current screen is My Admin Page
		$screen->add_help_tab(
			array(
				'id'	=> 'hi_help_tab',
				'title'	=> __('Help'),
				'content'	=> __( '<p>If we can be of any service, don\'t hesitate to let us know and we\'ll provide you with reliable, efficient help at critical times.</p>
<p>Please contact us at any time via Email: <a target="_blank" href="mailto:support@seawebster.com">support@seawebster.com</a></p>
<p>Product documentation is available online here: <a target="_blank" href="http://docs.seawebster.com/hover-effects-builder/">http://docs.seawebster.com/hover-effects-builder/</a></p>', 'hitd' )
			)
		 );

        $debug_i = array();

        $debug_i[] = "<h5>Please, add this information to your support request.</h5>";

        if( function_exists( 'get_bloginfo' ) ){
            $debug_i[] = "WP Version: " . get_bloginfo( 'version' ) . "<br>";
        }

        if( function_exists( 'wp_get_theme' ) ){
            $theme = wp_get_theme();

            $debug_i[] = "Theme: " .
            " | " . $theme->get( 'Name' ) .
            " | " . $theme->get( 'Version' ) .
            " | " . $theme->get( 'ThemeURI' ) .
            " | " . $theme->get( 'Author' ) .
            " | " . $theme->get( 'AuthorURI' ) . "<br>";
        }
        if( function_exists( 'get_option' ) ) {
            $debug_i[] = "Plugins: " . implode( ", ", get_option('active_plugins', array() ) ) . "<br>";
        }
        if( function_exists( 'phpversion' ) ) {
            $debug_i[] = "PHP Ver: " . phpversion();
        }

        $screen->add_help_tab(
            array(
                'id'	=> 'hi_Debug_tab',
                'title'	=> __('Debug Info'),
                'content' => '<p>'. implode("", $debug_i).'</p>'
            )
        );
    }

	function hi_admin_help_callback()
	{
	}

	/*
	 * Admin interface
	 * */
	function hi_admin_interface()
	{
		include_once( HIF_PATH . 'templates/template-admin.php' );
	}

	/*
	 * Load admin styles
	 * */
	function hi_admin_print_styles()
	{
		// minicolors: colorpicker
		wp_enqueue_style( 'hi-minicolors', HIF_URL . 'assets/css/minicolors/jquery.minicolors.css', false, HIF_VERSION );

		// prettyPhoto
		wp_enqueue_style( 'hi-pretty-photo', HIF_URL . 'assets/css/pretty-photo/css/prettyPhoto.css', false, HIF_VERSION );

		// icon font admin
		wp_enqueue_style( 'font-backend', HIF_URL . 'assets/css/font-backend/style.css', false, HIF_VERSION );

		// x-editable plugin
		wp_enqueue_style( 'x-editable', HIF_URL . 'assets/css/x-editable/css/bootstrap-editable.css', false, HIF_VERSION );

		// twitter bootstrap
		wp_enqueue_style( 'hi-bootstrap', HIF_URL . 'assets/css/hi-bootstrap.css', false, HIF_VERSION );

		// builder interface
		wp_enqueue_style( 'hi-interface', HIF_URL . 'assets/css/hi-backend.css', false, HIF_VERSION );
	}

	/*
	 * Load fonts
	 * */
	function hi_admin_print_styles_fonts()
	{
		// icon font front
		wp_enqueue_style( 'font-frontend', HIF_URL . 'assets/css/font-frontend/style.css', false, HIF_VERSION );
	}

	/*
    * Load admin scripts
    * */
	function hi_admin_print_scripts()
	{
		wp_enqueue_script( 'jquery' );

		// vendors libs
		wp_enqueue_script( 'hi-backend', HIF_URL . 'assets/js/hi-backend.min.js', array( 'jquery' ), HIF_VERSION );

		// builder
		wp_enqueue_script( 'hi-builder', HIF_URL . 'assets/js/hi-builder.min.js', array( 'jquery', 'hi-backend' ), HIF_VERSION );
	}
